import React, { useEffect, useState } from 'react';
import { getProducts } from '../api';
import Chat from './Chat';

const CustomerDashboard = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await getProducts(); // Call to API to fetch products
        if (Array.isArray(response.data.products)) {
          setProducts(response.data.products); // Set products only if it's an array
        } else {
          setProducts([]); // Set to an empty array if the response is not an array
        }
      } catch (error) {
        console.error('Error fetching products:', error);
        setProducts([]); // Default to an empty array if there's an error
      }
    };
  
    fetchProducts();
  }, []);

  return (
    <div>
    <h2>Customer Dashboard</h2>
    <h3>Available Products</h3>
    <ul>
      {Array.isArray(products) && products.length > 0 ? (
        products.map(product => (
          <li key={product._id}>
            <h4>{product.name}</h4>
            <p>Price: ${product.price}</p>
            <p>Quantity: {product.quantity}</p>
            <img src={`http://localhost:5000/${product.image}`} alt={product.name} style={{ width: '100px', height: 'auto' }} />
            {/* Add a button to negotiate */}
            <button onClick={() => handleNegotiation(product)}>Negotiate</button>
          </li>
        ))
      ) : (
        <p>No products available</p>
      )}
    </ul>
  </div>
);
};
export default CustomerDashboard;
