import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import axios from 'axios';

export default function Cart() {
  const location = useLocation();
  const cartItems = location.state ? location.state.cartItems : [];
  const { updateCart } = useCart(); // Use updateCart to update the cart items

  const [negotiationMessages, setNegotiationMessages] = useState({});
  const [negotiatedPrices, setNegotiatedPrices] = useState({});
  const [eligibleForNegotiation, setEligibleForNegotiation] = useState({}); // Track negotiation eligibility

  // Check eligibility for negotiation when cart items change
  useEffect(() => {
    const checkNegotiationEligibility = () => {
      let negotiationEligibility = {};
      cartItems.forEach((item) => {
        if (item.quantity >= item.minQuantityForNegotiation) {
          negotiationEligibility[item._id] = true;
        } else {
          negotiationEligibility[item._id] = false;
        }
      });
      setEligibleForNegotiation(negotiationEligibility);
    };

    checkNegotiationEligibility();
  }, [cartItems]);

  // Handle negotiation message change
  const handleNegotiationChange = (e, productId) => {
    setNegotiationMessages({
      ...negotiationMessages,
      [productId]: e.target.value,
    });
  };

  // Send negotiation request to farmer
  const sendNegotiation = async (productId) => {
    const message = negotiationMessages[productId];
    if (!message) return;

    try {
      const response = await axios.post('http://localhost:5000/api/negotiate', {
        productId,
        message,
      });
      if (response.data.success) {
        alert('Negotiation request sent to the farmer.');
      }
    } catch (error) {
      console.error('Error sending negotiation message:', error);
    }
  };

  // Handle the farmer's response with the negotiated price
  const handleFarmerResponse = (productId, negotiatedPrice) => {
    setNegotiatedPrices((prev) => ({
      ...prev,
      [productId]: negotiatedPrice,
    }));

    // Update the cart with the negotiated price
    updateCart(productId, negotiatedPrice);
  };

  return (
    <div>
      <h2>Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cartItems.map((item, index) => (
            <li key={index}>
              <h4>{item.name}</h4>
              <p>
                Price: $
                {negotiatedPrices[item._id] ? negotiatedPrices[item._id] : item.price}
              </p>
              <p>Quantity: {item.quantity}</p>

              {/* Show negotiation message input if eligible for negotiation */}
              {eligibleForNegotiation[item._id] && (
                <div>
                  <textarea
                    placeholder="Enter your negotiation message"
                    value={negotiationMessages[item._id] || ''}
                    onChange={(e) => handleNegotiationChange(e, item._id)}
                  />
                  <button onClick={() => sendNegotiation(item._id)}>
                    Send Negotiation
                  </button>
                </div>
              )}
            </li>
          ))}
        </ul>
      )}
      <button className="btn btn-primary">Proceed to Checkout</button>
    </div>
  );
}
