import React, { useState, useEffect } from 'react';
import { getProducts } from '../api';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import socket from '../socket';
import 'bootstrap/dist/css/bootstrap.min.css';

const CustomerDashboard = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [negotiationAvailable, setNegotiationAvailable] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const navigate = useNavigate(); // For navigation

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await getProducts(); // Fetch products from API
        setProducts(response.data.products); // Assuming your API returns the correct products array
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);

  // Function to add product to the cart
  const addToCart = (product) => {
    const existingProduct = cart.find(item => item._id === product._id);
    
    if (existingProduct) {
      // If product already in cart, update quantity
      existingProduct.quantity += 1;
      setCart([...cart]);
    } else {
      // Add new product to cart with quantity 1
      setCart([...cart, { ...product, quantity: 1 }]);
    }
    alert(`${product.name} added to cart!`);
  };

  // Function to open negotiation for a specific product
  const openNegotiation = (product) => {
    if (product.quantity >= product.minQuantityForNegotiation) {
      setNegotiationAvailable(true);
      setSelectedProduct(product); // Store the product being negotiated
    } else {
      alert('Negotiation not available for this product.');
    }
  };

  const handleMessageSend = async (e) => {
    e.preventDefault();
    if (message.trim()) {
      const messageData = {
        productId: selectedProduct._id,
        text: message,
        sender: 'customer', // Identify the sender as a customer
      };

      // Emit the message to the server
      socket.emit('sendMessage', messageData);

      // Save the message to the database
      try {
        await axios.post('http://localhost:5000/api/messages', messageData);
        setMessages((prevMessages) => [...prevMessages, messageData]); // Update local messages
        setMessage(''); // Clear input field
      } catch (error) {
        console.error('Error saving message:', error);
      }
    }
  };

  return (
    <div className="container">
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <a className="navbar-brand" href="/">US FARM</a>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <button className="btn btn-outline-success ml-2" onClick={() => navigate('/cart')}>
                <i className="fas fa-shopping-cart"></i> Cart ({cart.length})
              </button>
            </li>
            <li className="nav-item">
              <button className="btn btn-outline-primary" onClick={() => navigate('/support-form')}>
                Support
              </button>
            </li>
          </ul>
        </div>
      </nav>

      <h2 className="my-4">Welcome to Our Marketplace</h2>

      <div className="row">
        {Array.isArray(products) && products.length > 0 ? (
          products.map(product => (
            <div className="col-md-4 mb-4" key={product._id}>
              <div className="card h-100">
                <img src={`http://localhost:5000/${product.image}`} alt={product.name} style={{ width: '100px', height: 'auto' }} />
                <div className="card-body d-flex flex-column">
                  <h5 className="card-title">{product.name}</h5>
                  <p className="card-text">Price: ${product.price}</p>
                  <p className="card-text">Quantity Available: {product.quantity}</p>
                  <div className="mt-auto">
                    <button className="btn btn-primary btn-block" onClick={() => addToCart(product)}>
                      Add to Cart
                    </button>
                    <button 
                      className="btn btn-secondary btn-block mt-2" 
                      onClick={() => openNegotiation(product)}
                      disabled={product.quantity < product.minQuantityForNegotiation}
                    >
                      Negotiate with Farmer
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>No products available</p>
        )}
      </div>

      {/* Chat feature for negotiation */}
      {negotiationAvailable && selectedProduct && (
        <div>
          <h3>Negotiation for {selectedProduct.name}</h3>
          <div style={{ border: '1px solid #ccc', padding: '10px', height: '200px', overflowY: 'scroll' }}>
            {messages.map((msg, index) => (
              <div key={index} style={{ textAlign: msg.sender === 'customer' ? 'right' : 'left' }}>
                <strong>{msg.sender === 'customer' ? 'You' : 'Farmer'}:</strong> {msg.text}
              </div>
            ))}
          </div>
          <form onSubmit={handleMessageSend}>
            <input 
              type="text" 
              value={message} 
              onChange={(e) => setMessage(e.target.value)} 
              placeholder="Type your message..."
              required 
            />
            <button type="submit">Send</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default CustomerDashboard;
