import React, { useState, useEffect } from 'react';
import { getProducts, addProduct } from '../api';
import socket from '../socket';
import FarmerChat from './FarmerChat';

const FarmerDashboard = () => {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [quantity, setQuantity] = useState('');
  const [minQuantityForNegotiation, setMinQuantityForNegotiation] = useState('');
  const [image, setImage] = useState(null); // State to handle the image file
  const [selectedProductId, setSelectedProductId] = useState(null); // State to hold the selected product ID
  
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await getProducts(); // Fetch products from API
        setProducts(response.data.products);  // Set products into state
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };


    fetchProducts();

    socket.on('productAdded', (newProduct) => {
      setProducts((prevProducts) => [...prevProducts, newProduct]);
    });

    return () => socket.off('productAdded');
  }, []);
  const handleProductSelect = (productId) => {
    setSelectedProductId(productId);
  };
  const handleAddProduct = async (e) => {
    e.preventDefault();

    // Create form data to include image
    const formData = new FormData();
  formData.append('name', name);
  formData.append('price', price);
  formData.append('quantity', quantity);
  formData.append('minQuantityForNegotiation', minQuantityForNegotiation);
  formData.append('image', image); // Ensure this is a File object

  try {
    await addProduct(formData); // Calls the API to add the product
    // Reset fields after successful addition
    setName('');
    setPrice('');
    setQuantity('');
    setMinQuantityForNegotiation('');
    setImage(null); // Clear image
  } catch (error) {
    console.error('Error adding product:', error);
    alert('Error adding product. Please try again.');
  }
};

  return (
    <div>
      <h2>Farmer Dashboard</h2>
      <form onSubmit={handleAddProduct} encType="multipart/form-data">
        <input
          type="text"
          placeholder="Product Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="number"
          placeholder="Price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Quantity"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
        />
        <input
          type="number"
          placeholder="Min Quantity for Negotiation"
          value={minQuantityForNegotiation}
          onChange={(e) => setMinQuantityForNegotiation(e.target.value)}
        />
        <input
          type="file"
          onChange={(e) => setImage(e.target.files[0])} // Capture the image file
        />
        <button type="submit">Add Product</button>
      </form>

      <h3>Your Products</h3>
      <ul>
        {products.map(product => (
          <li key={product._id}>
            <h4>{product.name}</h4>
            <p>Price: {product.price}</p>
            <p>Quantity: {product.quantity}</p>
            <img src={`http://localhost:5000/${product.image}`} alt={product.name} style={{ width: '100px', height: 'auto' }} />
          </li>
        ))}
      </ul>
      {selectedProductId && (
        <FarmerChat productId={selectedProductId} farmerId="yourFarmerIdHere" /> )}{/* Replace with actual farmer ID */}
      
    </div>
  );
};

export default FarmerDashboard;
