import React from 'react';
import { loadStripe } from '@stripe/stripe-js';
import axios from 'axios';
import { useLocation } from 'react-router-dom'; // Import useLocation

const stripePromise = loadStripe('your_stripe_publishable_key');

const handleCheckout = async (cartItems) => {
  const stripe = await stripePromise;
  const { data } = await axios.post('http://localhost:5000/api/payment/create-checkout-session', { cartItems });

  const result = await stripe.redirectToCheckout({
    sessionId: data.id,
  });

  if (result.error) {
    alert(result.error.message);
  }
};

export default function Cart() {
  const location = useLocation();
  const cartItems = location.state ? location.state.cartItems : []; // Use location to get cartItems

  return (
    <div>
      <h2>Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cartItems.map((item, index) => (
            <li key={index}>
              <h4>{item.name}</h4>
              <p>Price: ${item.price}</p>
              <p>Quantity: {item.quantity}</p>
            </li>
          ))}
        </ul>
      )}
      <button onClick={() => handleCheckout(cartItems)}>Checkout</button>
    </div>
  );
}
