import React, { useState, useEffect } from 'react';
import socket from '../socket'; // Ensure you have your socket instance set up

const Chat = ({ productId, farmerId }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);

  // Listen for incoming messages from the farmer
  useEffect(() => {
    socket.on('receiveMessage', (messageData) => {
      setMessages((prevMessages) => [...prevMessages, messageData]);
    });

    return () => {
      socket.off('receiveMessage'); // Clean up the listener on unmount
    };
  }, []);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim()) {
      const messageData = {
        text: message,
        productId,
        farmerId, // Assuming you pass the farmer's ID as a prop
        sender: 'customer'
      };
      socket.emit('sendMessage', messageData); // Emit the message to the server
      setMessages((prevMessages) => [...prevMessages, messageData]); // Update local messages
      setMessage(''); // Clear the input
    }
  };

  return (
    <div>
      <h3>Chat for Product {productId}</h3>
      <div style={{ border: '1px solid #ccc', padding: '10px', height: '150px', overflowY: 'scroll' }}>
        {messages.map((msg, index) => (
          <div key={index} style={{ textAlign: msg.sender === 'customer' ? 'right' : 'left' }}>
            <strong>{msg.sender === 'customer' ? 'You' : 'Farmer'}:</strong> {msg.text}
          </div>
        ))}
      </div>
      <form onSubmit={sendMessage}>
        <input 
          type="text" 
          value={message} 
          onChange={(e) => setMessage(e.target.value)} 
          placeholder="Type your message..."
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default Chat;
