import React, { createContext, useContext, useState ,useEffect} from 'react';
import axios from 'axios';

const CartContext = createContext();

export const useCart = () => {
  return useContext(CartContext);
};

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const token = localStorage.getItem('token'); // Ensure the token is retrieved correctly
  const updateCart = (newCart) => setCart(newCart);  // Make sure `setCart` properly updates the cart

  // Fetch cart on component mount
  useEffect(() => {
    const fetchCart = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/cart', {
          headers: { Authorization: `Bearer ${token}` },  // Pass token in the headers
        });
        setCart(response.data.cart);  // Set cart state with fetched data
      } catch (error) {
        console.error('Error fetching cart:', error);
      }
    };

    fetchCart(); // Fetch cart when the component mounts
  }, [token]);  // Dependency array ensures this runs when token changes

  // Add product to cart or update quantity if it exists
  const addToCart = (product) => {
    setCart((prevCart) => {
      const existingProduct = prevCart.find(item => item._id === product._id);
      if (existingProduct) {
        // Update quantity if it exists
        return prevCart.map(item =>
          item._id === product._id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        // Add new product with initial quantity of 1
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });
  };

  // Update product price after negotiation
  const updateProductPrice = (productId, newPrice) => {
    setCart((prevCart) => {
      return prevCart.map(item =>
        item._id === productId ? { ...item, price: newPrice } : item
      );
    });
  };

  return (
    <CartContext.Provider value={{ cart, addToCart, updateProductPrice }}>
      {children}
    </CartContext.Provider>
  );
};
