import React, { useState, useEffect } from 'react';
import { getProducts } from '../api';
import Chat from './Chat';
import Cart from './Cart'; // Assuming Cart is a component
import axios from 'axios';
import socket from '../socket';


const CustomerDashboard = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]); // State to manage cart items
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const productId = "yourProductIdHere"; // Replace this with the actual product ID based on the context
  
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await getProducts(); // Fetch products from API
        setProducts(response.data.products);  // Assuming your API returns the correct products array
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);

  // Function to add product to the cart
  const addToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]); // Add the selected product to the cart
    alert(`${product.name} added to cart!`); // Notify user
  };
  useEffect(() => {
    // Fetch previous messages when the component mounts
    const fetchMessages = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/messages/${productId}`);
        setMessages(response.data.messages); // Set the fetched messages into state
      } catch (error) {
        console.error("Error fetching messages:", error);
      }
    };

    fetchMessages();


    // Listen for incoming messages
    socket.on('receiveMessage', (messageData) => {
      if (messageData.productId === productId) { // Check if the message is for this product
        setMessages((prevMessages) => [...prevMessages, messageData]);
      }
    });

    return () => {
      socket.off('receiveMessage'); // Clean up the listener on unmount
    };
  }, [productId]); // Adding productId as a dependency

  const sendMessage = async (e) => {
    e.preventDefault();
    if (message.trim()) {
      const messageData = {
        productId,
        text: message,
        sender: 'customer', // Identify the sender as a customer
      };

      // Emit the message to the server
      socket.emit('sendMessage', messageData);

      // Save the message to the database
      try {
        await axios.post('http://localhost:5000/api/messages', messageData); // Make sure this endpoint is correct
        setMessages((prevMessages) => [...prevMessages, messageData]); // Update local messages
        setMessage(''); // Clear the input field
      } catch (error) {
        console.error('Error saving message:', error);
      }
    }
  };
  return (
    <div>
    <h2>Customer Dashboard</h2>
    <Cart /> {/* Include the cart component */}
    <h3>Available Products</h3>
    <ul>
      {Array.isArray(products) && products.length > 0 ? (
        products.map(product => (
          <li key={product._id}>
            <h4>{product.name}</h4>
            <p>Price: ${product.price}</p>
            <p>Quantity: {product.quantity}</p>
            <img src={`http://localhost:5000/${product.image}`} alt={product.name} style={{ width: '100px', height: 'auto' }} />
            <Chat productId={product._id} farmerId={product.farmer} /> {/* Pass the actual product ID here */}
          </li>
        ))
      ) : (
        <p>No products available</p>
      )}
    </ul>
  </div>
);
};

export default CustomerDashboard;
