import React, { useState, useEffect, useRef } from 'react';
import socket from '../socket';
import axios from 'axios';

const SupportChat = ({ farmerId }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [image, setImage] = useState(null); // State for image upload
  const messagesEndRef = useRef(null); // Ref to automatically scroll to the bottom
  const token = localStorage.getItem('token');
  console.log('Token:', token);  // Check if the token is being fetched correctly
  // Auto-scroll to the bottom when messages change
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  // Fetch previous messages when the component mounts
  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/support/messages');
        setMessages(response.data.messages);
      } catch (error) {
        console.error('Error fetching support messages:', error);
      }
    };
    fetchMessages();

    // Listen for incoming messages
    socket.on('receiveMessage', (msg) => {
      setMessages((prevMessages) => [...prevMessages, msg]);
    });

    return () => {
      socket.off('receiveMessage');
    };
  }, []);

  const sendMessage = async (e) => {
    e.preventDefault();
  
    const formData = new FormData();
    formData.append('text', message);
    formData.append('sender', 'farmer');
    formData.append('farmerId', farmerId);
   
    // Append the image to formData if there's any image selected
    if (image) {
      formData.append('image', image);
    }
  
    try {
      // Emit the message to the server
      socket.emit('sendMessage', { text: message, sender: 'farmer', image: image ? image.name : null });
  
      // Get the auth token from local storage or wherever it's stored
      const token = localStorage.getItem('token');
  
      // Save the message (and image if uploaded) to the database with authorization header
      const response = await axios.post('http://localhost:5000/api/support/messages', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `Bearer ${token}`,  // Attach the token
        },
      });
  
      // Add new message to the chat
      setMessages((prevMessages) => [...prevMessages, response.data.message]);
      setMessage('');
      setImage(null); // Clear the image input after sending
  
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };
  

  return (
    <div>
      <h2>Support Chat</h2>
      <div style={{ border: '1px solid #ccc', padding: '10px', height: '300px', overflowY: 'scroll' }}>
        {messages.map((msg, index) => (
          <div key={index} style={{ textAlign: msg.sender === 'farmer' ? 'right' : 'left', margin: '10px' }}>
            <strong>{msg.sender}:</strong> {msg.text}
            {/* Display the image if present in the message */}
            {msg.image && (
              <div>
                <img 
                  src={`http://localhost:5000/uploads/${msg.image}`} 
                  alt="support-chat-img" 
                  style={{ maxWidth: '100px', marginTop: '5px' }} 
                />
              </div>
            )}
          </div>
        ))}
        {/* Reference for auto-scroll */}
        <div ref={messagesEndRef} />
      </div>

      <form onSubmit={sendMessage} style={{ marginTop: '10px' }}>
        <input 
          type="text" 
          value={message} 
          onChange={(e) => setMessage(e.target.value)} 
          placeholder="Type your message..." 
          required 
          style={{ width: '75%', marginRight: '10px' }}
        />
        <input 
          type="file" 
          onChange={(e) => setImage(e.target.files[0])} // Capture the image
          accept="image/*"
        />
        <button type="submit" style={{ marginLeft: '10px' }}>Send</button>
      </form>
    </div>
  );
};

export default SupportChat;
