// Cart.js

import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import axios from 'axios';

export default function Cart() {
  const { cart } = useCart();
  const [negotiationMessages, setNegotiationMessages] = useState({});
  const [requestedPrices, setRequestedPrices] = useState({});
  // Handle negotiation message change
  const handleNegotiationChange = (e, productId) => {
    setNegotiationMessages({
      ...negotiationMessages,
      [productId]: e.target.value,
    });
  };

   // Handle requested price change
   const handlePriceChange = (e, productId) => {
    setRequestedPrices({
      ...requestedPrices,
      [productId]: e.target.value,
    });
  };
  
  
  // Send negotiation request to farmer
  const sendNegotiation = async (productId) => {
    const message = negotiationMessages[productId];
    if (!message) return;

    try {
      const response = await axios.post('http://localhost:5000/api/negotiate', {
        productId,
        message,
        requestedPrice: cart.find(item => item.productId === productId).price, // Add the requested price here
        farmerId: cart.find(item => item.productId === productId).farmerId // Pass farmerId
      });

      if (response.data.success) {
        alert('Negotiation request sent to the farmer.');
      }
    } catch (error) {
      console.error('Error sending negotiation message:', error);
    }
  };

  return (
    <div>
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cart.map((item, index) => (
            <li key={index}>
              <h4>{item.name}</h4>
              <p>Price: ${item.price}</p>
              <p>Quantity: {item.quantity}</p>

              {/* Show negotiation message input if eligible for negotiation */}
              {item.quantity >= item.minQuantityForNegotiation && (
                <div>
                  <textarea
                    placeholder="Enter your negotiation message"
                    value={negotiationMessages[item._id] || ''}
                    onChange={(e) => handleNegotiationChange(e, item._id)}
                  />
                  <button onClick={() => sendNegotiation(item._id)}>
                    Send Negotiation
                  </button>
                </div>
              )}
            </li>
          ))}
        </ul>
      )}
      <button className="btn btn-primary">Proceed to Checkout</button>
    </div>
  );
}
