import React, { useState, useEffect } from 'react';
import { getProducts, addProduct } from '../api';
import socket from '../socket';
import FarmerChat from './FarmerChat';

const FarmerDashboard = () => {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [quantity, setQuantity] = useState('');
  const [minQuantityForNegotiation, setMinQuantityForNegotiation] = useState('');
  const [image, setImage] = useState(null);
  
  // Get the farmer's ID from local storage or a context
  const farmerId = localStorage.getItem('farmerId') || "defaultFarmerId"; // Replace with your method to get the farmer ID

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await getProducts();
        setProducts(response.data.products);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();

    // Handle new product additions via Socket.IO
    socket.on('productAdded', (newProduct) => {
      setProducts((prevProducts) => [...prevProducts, newProduct]);
    });

    return () => socket.off('productAdded');
  }, []);

  const handleAddProduct = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', name);
    formData.append('price', price);
    formData.append('quantity', quantity);
    formData.append('minQuantityForNegotiation', minQuantityForNegotiation);
    formData.append('image', image);

    try {
      await addProduct(formData);
      setName('');
      setPrice('');
      setQuantity('');
      setMinQuantityForNegotiation('');
      setImage(null);
    } catch (error) {
      console.error('Error adding product:', error);
      alert('Error adding product. Please try again.');
    }
  };

  return (
    <div>
      <h2>Farmer Dashboard</h2>
      <form onSubmit={handleAddProduct} encType="multipart/form-data">
        <input type="text" placeholder="Product Name" value={name} onChange={(e) => setName(e.target.value)} />
        <input type="number" placeholder="Price" value={price} onChange={(e) => setPrice(e.target.value)} />
        <input type="number" placeholder="Quantity" value={quantity} onChange={(e) => setQuantity(e.target.value)} />
        <input type="number" placeholder="Min Quantity for Negotiation" value={minQuantityForNegotiation} onChange={(e) => setMinQuantityForNegotiation(e.target.value)} />
        <input type="file" onChange={(e) => setImage(e.target.files[0])} />
        <button type="submit">Add Product</button>
      </form>

      <h3>Your Products</h3>
      <ul>
        {products.map(product => (
          <li key={product._id}>
            <h4>{product.name}</h4>
            <p>Price: {product.price}</p>
            <p>Quantity: {product.quantity}</p>
            <img src={`http://localhost:5000/${product.image}`} alt={product.name} style={{ width: '100px', height: 'auto' }} />
            <FarmerChat productId={product._id} farmerId={farmerId} />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FarmerDashboard;
