import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import axios from 'axios';

export default function Cart() {
  const { cart } = useCart();
  const [negotiationMessages, setNegotiationMessages] = useState({});
  const [negotiationStatus, setNegotiationStatus] = useState({}); // Track negotiation status

  const handleNegotiationChange = (e, productId) => {
    setNegotiationMessages({
      ...negotiationMessages,
      [productId]: e.target.value,
    });
  };

  const sendNegotiation = async (productId) => {
    const message = negotiationMessages[productId];
    if (!message) return;

    try {
      const response = await axios.post('http://localhost:5000/api/negotiate', {
        productId,
        message,
      });
      if (response.data.success) {
        alert('Negotiation request sent to the farmer.');
        // Reset the message after sending
        setNegotiationMessages((prev) => ({ ...prev, [productId]: '' }));
        setNegotiationStatus((prev) => ({ ...prev, [productId]: 'sent' }));
      } else {
        alert('Failed to send negotiation request.');
      }
    } catch (error) {
      console.error('Error sending negotiation message:', error);
      alert('An error occurred while sending the negotiation request.');
    }
  };

  return (
    <div>
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cart.map((item) => (
            <li key={item._id}>
              <h4>{item.name}</h4>
              <p>Price: ${item.price}</p>
              <p>Quantity: {item.quantity}</p>
              {item.quantity >= item.minQuantityForNegotiation && (
                <div>
                  <textarea
                    placeholder="Enter your negotiation message"
                    value={negotiationMessages[item._id] || ''}
                    onChange={(e) => handleNegotiationChange(e, item._id)}
                  />
                  <button onClick={() => sendNegotiation(item._id)}>
                    Send Negotiation
                  </button>
                  {negotiationStatus[item._id] === 'sent' && <p>Message sent!</p>}
                </div>
              )}
            </li>
          ))}
        </ul>
      )}
      <button className="btn btn-primary">Proceed to Checkout</button>
    </div>
  );
}
