import React, { useState, useEffect } from 'react';
import { getProducts } from '../api';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useCart } from '../context/CartContext'; // Import the useCart hook

const CustomerDashboard = () => {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate(); // For navigation
  const { addToCart, cart } = useCart(); // Use addToCart and cart from CartContext

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await getProducts(); // Fetch products from API
        setProducts(response.data.products); // Assuming your API returns the correct products array
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div className="container">
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <a className="navbar-brand" href="/">US FARM</a>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ml-auto">
            {/* Cart Button */}
            <li className="nav-item">
              <button className="btn btn-outline-success ml-2" onClick={() => navigate('/cart')}>
                <i className="fas fa-shopping-cart"></i> Cart ({cart.length})
              </button>
            </li>
            {/* Support Button */}
            <li className="nav-item">
              <button className="btn btn-outline-primary ml-2" onClick={() => navigate('/support')}>
                <i className="fas fa-headset"></i> Support
              </button>
            </li>
          </ul>
        </div>
      </nav>

      <h2 className="my-4">Welcome to Our Marketplace</h2>

      <div className="row">
        {Array.isArray(products) && products.length > 0 ? (
          products.map(product => (
            <div className="col-md-4 mb-4" key={product._id}>
              <div className="card h-100">
                <img src={`http://localhost:5000/${product.image}`} alt={product.name} style={{ width: '100px', height: 'auto' }} />
                <div className="card-body d-flex flex-column">
                  <h5 className="card-title">{product.name}</h5>
                  <p className="card-text">Price: ${product.price}</p>
                  <p className="card-text">Quantity: {product.quantity}</p>
                  <div className="mt-auto">
                    {/* Add to Cart Button */}
                    <button className="btn btn-primary btn-block" onClick={() => addToCart(product)}>
                      Add to Cart
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>No products available</p>
        )}
      </div>
    </div>
  );
};

export default CustomerDashboard;
